mapper.select:
SELECT count(*)
FROM R
WHERE (r_rental_status_id IN (2, 3)) AND r_c_id = #{c_id}

mapper.update:
UPDATE R
SET r_order_status_id = 2
WHERE r_o_id = #{o_id}

UPDATE RR
SET rr_res_status_id = 4
WHERE rr_id = #{rr_id}

UPDATE Rental_Res
SET rr_res_status_id = (
    SELECT res_status_id
    FROM Status_RR
    WHERE res_status = '예약불가'
    ),
    RR_REJECTION_REASON = #{rr_rejection_reason}
WHERE rr_id = #{rr_id}

UPDATE Rental
SET R_DELIVERY_STATUS_ID = (
    SELECT DELIVERY_STATUS_ID
    FROM Status_D
    WHERE DELIVERY_STATUS = #{delivery_status}
)
WHERE r_o_id = #{o_id}
AND R_B_ISBN = (
    SELECT b_isbn from book where b_title = #{b_title} and b_s_id = 'seller001'
)

UPDATE Rental r
SET r.R_RENTAL_STATUS_ID = (
    SELECT MAX(RENTAL_STATUS_ID)
    FROM Status_R
    WHERE RENTAL_STATUS = '연체'
)
WHERE r_o_id = #{o_id}
    AND (
            (
            SELECT MAX(o_date + r_rentalperiod) FROM order_list WHERE o_id = #{o_id} AND b_title = #{b_title}
            ) < sysdate
    )

UPDATE Rental_Res r
SET r.RR_RES_STATUS_ID = (
    SELECT MAX(s.RES_STATUS_ID)
    FROM Status_RR s
    WHERE s.RES_STATUS = '대여가능'
)
WHERE r.RR_S_ID = 'seller001'
AND r.RR_B_ISBN IN (
    SELECT b_isbn FROM book WHERE b_title = #{b_title}
)
AND r.RR_REQDATE IN (
    SELECT MIN(RR_REQDATE)
    FROM Rental_Res rr
    JOIN book b ON rr.RR_B_ISBN = b.b_isbn
    WHERE rr.RR_S_ID = 'seller001' AND b.b_title = #{b_title}
)
